function on_err() {
//me.frm.refresh();
   console.log('submitted fail');
}
function on_succ() {
 console.log('submitted');
}

function needs_submit_callback(r) {
      console.log(r);
      console.log('message:' + r.message[0]);
      if (r.message[0] == 'true') {
         cur_frm.savesubmit(null, on_succ, on_err);
         $('button').each(function () {
            console.log($(this).text());
         });
         frappe.call({
             method: "fedex_shipment.digitools.delno_need_submit",
             args: {should_submit:false},
             callback: function (r) {console.log(r); }
         });
      } else setTimeout(main_loop, 2000);
}

function main_loop() {
   frappe.call({
      method: "fedex_shipment.digitools.delno_need_submit",
      callback: needs_submit_callback
   });
}
main_loop();

